function UIPlayer(options) {

    options = options instanceof Object ? options : {};

    const $S = options.$SELECTORS;

    const mediator = options.mediator;
    const EVENTS = mediator.EVENTS;
    const TRIGGERS = mediator.TRIGGERS;

    let user = null;

    let timestampIntervalId = null;

    let canMove = false;
    let changeVolume = false;
    let currentVolume = null;


    const audio = new Audio();

    /**
     * Найти песню у пользователя
     */
    function findSong(songId) {
        let song = user.songs.find(s => s.id === songId);
        if (!song) {
            for (const playlist of user.playlists) {
                song = playlist.songs.find(s => s.id === songId);
            }
        }
        return song;
    }

    /**
     * Заполнить первоначально плеер
     */
    function fillStartingProgressBar() {
        $S.PLAYER.PROGRESS.css('transform', 'scaleX(0)');
        $S.PLAYER.VOLUME.css('transform', 'scaleX(0)');
        $S.PLAYER.MARKER.css('left', '0');
        $S.PLAYER.VOLUME_MARKER.css('left', '0');
    }

    /**
     * Заполнить информацию о песне
     * @param song
     */
    function fillAboutSong(song) {
        $S.PLAYER.SONG_NAME.empty().append(song.name.split(' - ')[1].split('.')[0]);
        $S.PLAYER.PERFORMER.empty().append(song.name.split(' - ')[0]);
        $($('.like-playing-song-svg').children('path')[2]).css('fill', `${(mediator.get(TRIGGERS.IS_SONG_LIKED, song.id)) ? "#5ab3b8" : "#fff"}`)
    }

    /**
     * Записать время
     */
    function fillTimestamp() {
        let minutes = parseInt(audio.duration / 60, 10);
        let seconds = parseInt(audio.duration % 60);
        const duration = `${minutes ? minutes : '0'}:${seconds ? (seconds < 10 ? '0' + seconds : seconds) : '00'}`;
        minutes = parseInt(audio.currentTime / 60, 10);
        seconds = parseInt(audio.currentTime % 60);
        const currentTime = `${minutes ? minutes : '0'}:${seconds ? (seconds < 10 ? '0' + seconds : seconds) : '00'}`;
        $S.PLAYER.TIMESTAMP.empty().append(`${currentTime} / ${duration}`);
    }

    /**
     * Длина песни 180 секунд. Сейчас 5 секунда. 100% => 180, x% => 5. x = 100% * 5 / 180
     * Длина песни 180 секунд. Сейчас 5% прошло. 100% => 180, 5% => x. x = 180 * 5 / 100
     */
    /**
     * Заполнить прогресс
     */
    function fillProgress() {
        const currentPosition = 100 * audio.currentTime / audio.duration;
        $S.PLAYER.PROGRESS.css('transform', `scaleX(${currentPosition / 100})`);
        $S.PLAYER.MARKER.css('left', `calc(${currentPosition}% - 30px)`);
    }

    /**
     * Подвинуть маркер
     * @param e
     */
    function moveMarker(e) {
        const posX = 100 * e.clientX / window.innerWidth;
        $S.PLAYER.PROGRESS.css('transform', `scaleX(${posX / 100})`);
        $S.PLAYER.MARKER.css('left', `calc(${posX}% - 30px)`);
    }

    function timestampIntervalHandler() {
        if (audio.currentTime >= audio.duration) {
            clearInterval(timestampIntervalId);
            mediator.call(EVENTS.PLAY_NEXT_SONG);
        }
        audio.currentTime += 0.250;
        fillTimestamp(audio);
        fillProgress(audio);
    }

    /**
     * Перемотать песню
     * @param e
     * @param song
     */
    function setNewPosition(e, song) {
        if (canMove) {
            const posXPercent = 100 * e.clientX / window.innerWidth;
            audio.currentTime = (posXPercent * audio.duration / 100).toFixed(2);
            mediator.call(EVENTS.SET_NEW_SONG_POSITION, {songId: song.id, position: audio.currentTime});
            timestampIntervalId = setInterval(timestampIntervalHandler, 250, audio);
            canMove = false;
        }
    }

    /**
     * Обработчик событий на прогресс баре песни
     * @param song
     */
    function progressBarHandler(song) {
        $S.PLAYER.MARKER.off('mousedown touchstart').on('mousedown touchstart', () => {
            clearInterval(timestampIntervalId);
            canMove = true;
        });
        $S.MAIN_CONTAINER.off('mouseup touchend').on('mouseup touchend', e => setNewPosition((e.originalEvent.changedTouches) ? e.originalEvent.changedTouches[0] : e.originalEvent, song));
        $S.PLAYER.PLAYER_CONTAINER.off('mouseup touchend').on('mouseup touchend', e => setNewPosition((e.originalEvent.changedTouches) ? e.originalEvent.changedTouches[0] : e.originalEvent, song));
        $S.PLAYER.MARKER.off('mouseup touchend').on('mouseup touchend', e => setNewPosition((e.originalEvent.changedTouches) ? e.originalEvent.changedTouches[0] : e.originalEvent, song));
        $S.PLAYER.PLAYER_CONTAINER.off('mousemove touchmove').on('mousemove touchmove', e => {
            if (canMove) {
                moveMarker((e.originalEvent.changedTouches) ? e.originalEvent.changedTouches[0] : e.originalEvent);
            }
        });
        $S.MAIN_CONTAINER.off('mousemove touchmove').on('mousemove touchmove', e => {
            if (canMove) {
                moveMarker((e.originalEvent.changedTouches) ? e.originalEvent.changedTouches[0] : e.originalEvent);
            }
        });
        $S.PLAYER.PROGRESS_BAR.off('mousemove touchmove').on('mousemove touchmove', e => {
            if (canMove) {
                moveMarker((e.originalEvent.changedTouches) ? e.originalEvent.changedTouches[0] : e.originalEvent);
            }
        });
    }

    /**
     * Заполнить прогресс бар
     * @param song
     */
    function fillProgressBar(song) {
        fillTimestamp();
        timestampIntervalHandler();
        timestampIntervalId = setInterval(timestampIntervalHandler, 250);
        progressBarHandler(song);
    }

    /**
     * Обработчик кнопок
     */
    function buttonsHandler(song) {
        let currentTime = null;
        $S.PLAYER.MAIN_BTN.empty().html(`<image class="main-btn-image"  x="0px" y="0px" width="0px" height="0px"  xlink:href="data:img/png;base64,"></image>`);
        $S.PLAYER.MAIN_BTN.off('click').on('click', function () {
            if (timestampIntervalId) {
                //ставить на паузу
                clearInterval(timestampIntervalId);
                timestampIntervalId = null;
                currentTime = audio.currentTime;
                mediator.call(EVENTS.PAUSE_SONG, song.id);
                $S.PLAYER.MAIN_BTN.empty().html(`<image class="main-btn-image" x="0px" y="0px" width="0px" height="0px" xlink:href="data:img/png;base64,"/>`);
            } else {
                //снимать с паузы
                mediator.call(EVENTS.RESUME_SONG, song.id);
                $S.PLAYER.MAIN_BTN.empty().html(`<image class="main-btn-image"  x="0px" y="0px" width="0px" height="0px"  xlink:href="data:img/png;base64,"></image>`);
                audio.currentTime = currentTime;
                timestampIntervalId = setInterval(timestampIntervalHandler, 250, audio);
            }
        });
        $S.PLAYER.PREV_SONG.off('click').on('click', () => {
            mediator.call(EVENTS.PLAY_PREV_SONG);
        });
        $S.PLAYER.NEXT_SONG.off('click').on('click', () => {
            mediator.call(EVENTS.PLAY_NEXT_SONG);
        });
    }

    function moveVolumeMarker(position) {
        const elemWidth = $S.PLAYER.VOLUME_BAR.innerWidth();
        let posX = 100 *  position / elemWidth;
        if (posX >= 100) {
            posX = 100;
        }
        if (posX < 0) {
            posX = 0;
        }
        $S.PLAYER.VOLUME.css('transform', `scaleX(${posX / 100})`);
        $S.PLAYER.VOLUME_MARKER.css('left', `calc(${posX}% - 10px)`);
        currentVolume = posX / 100;
    }

    /**
     * Обработчик громкости
     */
    function volumeHandler(song) {
        $S.PLAYER.VOLUME_MARKER.off('mousedown touchstart').on('mousedown touchstart', () => changeVolume = true);
        $S.PLAYER.VOLUME_BAR.off('mouseup touchend').on('mouseup touchend', e => {
            changeVolume = false;
            mediator.call(EVENTS.SET_VOLUME, {songId: song.id, volume: currentVolume});
        });
        $S.PLAYER.VOLUME_BAR.off('mousemove touchmove').on('mousemove touchmove', e => {
            if (changeVolume) {
                const clientX = (e.originalEvent.changedTouches) ? e.originalEvent.changedTouches[0].clientX : e.originalEvent.clientX;
                moveVolumeMarker(clientX - $S.PLAYER.VOLUME_BAR.position().left);
            }
        });
    }

    /**
     * Обработчик лайка
     */
    function likeHandler(song) {
        const _song = user.playlists.find(p => p.name === 'favorite').songs.find(s => s.id === song.id);
        $S.PLAYER.LIKE[0].dataset.liked = (_song) ? 'true' : 'false';
        $S.PLAYER.LIKE.off('click').on('click', async function () {
            const isLiked = this.dataset.liked;
            if (isLiked === 'false') {
                //like
                const result = await mediator.get(TRIGGERS.ADD_SONG_TO_FAVORITE, song.id);
                if (result && !result.error) {
                    $($(this).children('path')[2]).css('fill', '#5ab3b8');
                    this.dataset.liked = true;
                    mediator.call(EVENTS.USER_UPDATED);
                }
            } else {
                //unlike
                const result = await mediator.get(TRIGGERS.DELETE_SONG_FROM_FAVORITE, song.id);
                if (result && !result.error) {
                    $($(this).children('path')[2]).css('fill', '#fff');
                    this.dataset.liked = false;
                    mediator.call(EVENTS.USER_UPDATED);
                }
            }
        });
    }

    /**
     * Заполнить плеер
     * @param songId
     */
    async function fillPlayer(songId) {
        fillStartingProgressBar();
        songId = songId - 0;
        if (songId) {
            user = mediator.get(TRIGGERS.GET_USER);
            let song = findSong(songId);
            if (song) {
                const songData = await mediator.get(TRIGGERS.GET_SONG, song.id);
                audio.src = `data:audio/mp3;base64,${songData.data}`;
                if (song) {
                    clearInterval(timestampIntervalId);
                    timestampIntervalId = null;
                    fillAboutSong(song);
                    fillProgressBar(song);
                    buttonsHandler(song);
                    volumeHandler(song);
                    likeHandler(song);
                }
            }
        }
    }

    function resetPlayer() {
        clearInterval(timestampIntervalId);
        timestampIntervalId = null;
        audio.src = "";
        $S.PLAYER.MAIN_BTN.off('click');
        $S.PLAYER.PREV_SONG.off('click');
        $S.PLAYER.NEXT_SONG.off('click');
        $S.PLAYER.LIKE.off('click');
        $S.PLAYER.VOLUME_MARKER.off('mousedown touchstart');
        $S.PLAYER.VOLUME_BAR.off('mouseup touchend');
        $S.PLAYER.VOLUME_BAR.off('mousemove touchmove');
        $S.PLAYER.MARKER.off('mousedown touchstart');
        $S.PLAYER.PLAYER_CONTAINER.off('mouseup touchend');
        $S.PLAYER.MARKER.off('mouseup touchend');
        $S.PLAYER.PLAYER_CONTAINER.off('mousemove touchmove');
        $S.PLAYER.PROGRESS_BAR.off('mousemove touchmove');
        $S.MAIN_CONTAINER.off('mouseup touchend');
        $S.MAIN_CONTAINER.off('mousemove touchmove');
        $S.PLAYER.TIMESTAMP.empty().append('0:00 / 0:00');
        $S.PLAYER.MAIN_BTN.empty().html(`<image class="main-btn-image" x="0px" y="0px" width="0px" height="0px" xlink:href="data:img/png;base64,"/>`);
    }

    function init() {
        fillStartingProgressBar();
        mediator.subscribe(EVENTS.FILL_PLAYER, fillPlayer);
        mediator.subscribe(EVENTS.RESET_PLAYER, resetPlayer);
    }
    init();

}